import { React, useState } from "react";
import Form from "react-bootstrap/Form";
import { Button, FormControl, InputGroup } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

// const handleEmailInputChange = (event) => {
//   event.persist();
//   setValues((values) => ({
//     ...values,
//     email: event.target.value,
//   }));
// };

// const handlePasswordInputChange = (event) => {
//   event.persist();
//   setValues((values) => ({
//     ...values,
//     password: event.target.value,
//   }));

function Register() {
  const [Name, setName] = useState("Enter Name");
  const [Email, setEmail] = useState("Enter Email");
  const [Password, setPassword] = useState("Enter Password");
  const [RegisteredStatus, setRegisteredStatus] = useState("");

  async function handlesubmit() {
    const { status } = await axios.post("http://localhost:4000/register", {
      uname: Name,
      uemail: Email,
      upassword: Password,
    });
    console.log(status);
    if (toString(status) == toString(200)) {
      setRegisteredStatus("User has been successfully registered");
    }
    // console.log(Name);
    // console.log(Email);
    // console.log(Password);
  }

  return (
    <div>
      <Form className="Login-form text-center">
        <Form.Group className="mb-3">
          <Form.Label> Enter Your Name</Form.Label>
          <FormControl
            type="name"
            aria-label="First name"
            placeholder="First and last name"
            value={Name}
            onChange={(e) => {
              setName(e.target.value);
            }}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label> Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Sample@gmail.com"
            value={Email}
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Sample password"
            value={Password}
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
        </Form.Group>

        <Button variant="primary" type="submit" onClick={handlesubmit}>
          Submit
        </Button>

        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          Already a User <Link to="/login">Login here</Link>
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <span style={{ color: "green" }}>{RegisteredStatus}</span>
        </Form.Group>
      </Form>
    </div>
  );
}

export default Register;
